import React, { Component, useEffect } from 'react';
import {Line} from 'react-chartjs-2';
import Chart from 'chart.js';

import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container'

class Charts extends Component{

    constructor(props){
        super(props);
        this.state = {  
            data: []
        }
        //console.log(this.props)
    }

     componentDidMount(){
        this.getData()
    }

     getData(){
        // console.log(this.props)
       fetch(`http://localhost:4006/Assets/${this.props.asset}/${this.props.dept}/${this.props.meter}`)
       .then(res => res.json())
       .then(data => this.setState({data}))       
        
    }

    render(){

        const {data} = this.state;
        console.log(data)
        
        const Dates = []
        const Readings = []
        const Goal = []

        {data.map ((item)=>{
            Dates.push(item.a_readdate)
            Readings.push(item.a_meterreading)
            Goal.push(item.a_goal)
        })}

        //console.log(Dates, Readings)

        const dataSet = {
            labels: Dates,
            datasets:[
                {
                    label: 'Meter Reading',
                    data: Readings,
                    fill: true,                    
                },
                {
                    label: 'Goal',
                    data: Goal,
                    fill: false,
                }

            ]
        };

                           
        
        return (
            <div>
                <div>
                {this.state.data.map ((item)=>{
                    return(
                    <Container>
                        <Row>Project Id: {item.a_}</Row>
                    </Container>
                 ) })}
                </div> 
                <div>
                    <Line data = {dataSet} />
                </div>
            </div>
            
        )
    }
    
}

export default Charts;
